package com.galang.kisah25nabi.uiimport androidx.lifecycle.MutableLiveDataimport androidx.lifecycle.ViewModelimport com.galang.kisah25nabi.data.KisahResponseimport com.galang.kisah25nabi.data.network.ApiClientimport io.reactivex.rxjava3.android.schedulers.AndroidSchedulersimport io.reactivex.rxjava3.schedulers.Schedulers//Bisnis Logic / Data Layer = Nyimpen Data//Domain Layer = Memilah Data Untuk Ke UI LAYER dan Ke Data Layerclass MainViewModel : ViewModel() {    //LIVE DATA    //LIVE DATA = menginfomasikan data secara langsung    val kisahResponse = MutableLiveData<List<KisahResponse>>()    val isLoading = MutableLiveData<Boolean>()    val isError = MutableLiveData<Throwable>()    //RXJAVA    //RX JAVA = custom error    private fun getKisahNabi(responseHandler: (List<KisahResponse>) -> Unit, errorHandler:    (Throwable) -> Unit){        ApiClient.getApiService().getKisahNabi()            .subscribeOn(Schedulers.io())                //Menyediakan Proses Asynchronus      API -> get data dan mau taro di mana data ini            .observeOn(AndroidSchedulers.mainThread())                //Mau di tampilkan di mana data ini            .subscribe({                //handle sukses or error nya                //INI JIKA SUKSES                responseHandler(it)            },{                //INI JIKA ERROR                errorHandler(it)            })    }    fun getData(){        isLoading.value = true        getKisahNabi({            isLoading.value = true            kisahResponse.value = it        },{            isLoading.value = false            isError.value = it        })    }}